import React, { Component } from 'react';
import {Card, CardContent, Typography, Grdi, Grid} from "@material-ui/core"
import CountUp from "react-countup"
import cx from "classnames"
import styles from "./Cards.module.css"


const Cards = (props) => {
    console.log(props.data);

    if(!props.data.confirmed){
        return "Loading..."
    }
    return ( 

        <div className={styles.container}>
            <Grid container spacing={3} justify="center">
                <Grid item component={Card} xs={12} md={3} className={cx(styles.card, styles.infected)}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Infected</Typography>
                        <Typography variant="h5">
                            <CountUp 
                            start={0} 
                            end={props.data.confirmed.value}
                            duration = {1.2}
                            separator=","
                            />
                        </Typography>
                        <Typography color="textSecondary">{new Date(props.data.lastUpdate).toDateString()}</Typography>
                        <Typography variant="body2">Number of active cases as of now</Typography>
                    </CardContent>
                </Grid>
                <Grid item component={Card}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Recovered</Typography>
                        <Typography variant="h5">
                            <CountUp 
                            start={0} 
                            end={props.data.recovered.value}
                            duration = {1.2}
                            separator=","
                            />
                        </Typography>
                        <Typography color="textSecondary">{new Date(props.data.lastUpdate).toDateString()}</Typography>
                        <Typography variant="body2">Number of recoveries till date</Typography>
                    </CardContent>
                </Grid>
                <Grid item component={Card}>
                    <CardContent>
                        <Typography color="textSecondary" gutterBottom>Deaths</Typography>
                        <Typography variant="h5">
                            <CountUp 
                            start={0} 
                            end={props.data.deaths.value}
                            duration = {1.2}
                            separator=","
                            />
                        </Typography>
                        <Typography color="textSecondary">{new Date(props.data.lastUpdate).toDateString()}</Typography>
                        <Typography variant="body2">Number of confirmed deaths</Typography>
                    </CardContent>
                </Grid>
            </Grid>
        </div>
     );
}

export default Cards;
